import { useRef,useState } from "react";
import Alert from '@mui/material/Alert';
import './login.css'
function Login(){
    const usernameRef= useRef();
    const pwdRef  = useRef();
    const [valid,setValid] = useState(true);
    function handlerSubmit(e){
        e.preventDefault();
        let form = {
            username:usernameRef.current.value.trim(),
            pwd:pwdRef.current.value.trim(),
        };
        if(!form.username||!form.pwd){
            setValid(false);
        }else{

        }
        console.log(form);
    }
    return(
        <div className="container flex flex_ver flex_center_all">
            <div>Welcome, Recycler</div>
            <form onSubmit={handlerSubmit}>
                <div className="labelBox">
                    <label htmlFor="true">User Name</label>
                    <input type="text" required id="username" ref={usernameRef} />
                </div>
                <div className="labelBox">
                    <label htmlFor="true">Password</label>
                    <input type="password" required id="pwd" ref={pwdRef} />
                </div>
                <div>
                    <button onClick={handlerSubmit}>Login</button>
                </div>
            </form>
            {!valid && <Alert severity="error">Sorry, can not find this user</Alert>}
        </div>
    );
}
export default Login;
